import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { useUserRoleAccess } from "../Functions/apiUtils";
import { getUserInfo } from "../Functions/authUtils";

export default function UserCoursePage() {
    const [userJson, setUserJson] = useState(null);
    const { userId } = getUserInfo();

    useEffect(() => {
        fetchUserJson();
    }, []);

    const fetchUserJson = () => {
        fetch(`http://localhost:5000/get_user_json/${userId}`)
            .then((response) => response.json())
            .then((data) => {
                if (data.comment_on_results) {
                    setUserJson(JSON.parse(data.comment_on_results));
                }
            })
            .catch((error) => console.error("Error fetching user JSON:", error));
    };

    return (
        <div>
            <h1>User Course Page</h1>
            <Link to="/user_main_page" className="btn btn-primary">
                Back to Main Page
            </Link>
            <hr />
            {userJson && (
                <div className="card my-3">
                    <div className="card-header">
                        <h3>Course Outline</h3>
                    </div>
                    <div className="card-body">
                        <p>
                            <strong>Profession Description:</strong> {userJson.course_outline.profession_description.name}
                        </p>
                        <p>
                            <strong>Description:</strong> {userJson.course_outline.profession_description.description}
                        </p>
                        {/* You can add more details from the JSON file here */}
                    </div>
                </div>
            )}
        </div>
    );
}
