// apiUtils.js
export const fetchData = async (url, setData, dataKey) => {
  try {
    const response = await fetch(url);
    const data = await response.json();
    console.log("Fetched data:", data);

    if (Array.isArray(data[dataKey])) {
      setData(data[dataKey]);
    }
  } catch (error) {
    console.error(`Error fetching data from ${url}:`, error);
  }
};

export const renderListItems = (items, renderFunction) => (
  <ul>
    {Array.isArray(items) ? items.map((item) => (
      <li key={item.id}>
        {renderFunction(item)}
      </li>
    )) : null}
  </ul>
);

export const renderListItemsWithActions = (items, renderFunction, deleteHandler, modifyHandler) => (
  <ul>
    {Array.isArray(items) ? items.map((item) => (
      <li key={item.id}>
        {renderFunction(item)}
        <button onClick={() => deleteHandler(item.id)}>Delete</button>
        <button onClick={() => modifyHandler(item)}>Modify</button>
      </li>
    )) : null}
  </ul>
);
