import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

export default function UserCoursePage() {
    const [courseOutline, setCourseOutline] = useState(null);

    useEffect(() => {
        // Fetch the JSON data from your server
        fetchCourseOutline();
    }, []);

    const fetchCourseOutline = () => {
        // Make a request to your server to get the JSON data
        fetch(`http://localhost:5000/get_user_json/${userId}`)
            .then((response) => response.json())
            .then((data) => {
                if (data.user_jsons && data.user_jsons.length > 0) {
                    // Assuming the JSON data contains only one course outline
                    setCourseOutline(data.user_jsons[0]);
                }
            })
            .catch((error) => console.error("Error fetching course outline:", error));
    };

    return (
        <div>
            <h1>Java Developer Course Outline</h1>
            <Link to="/user_main_page" className="btn btn-primary">
                Back to Main Page
            </Link>
            <hr />
            {courseOutline && (
                <div>
                    <h2>Profession Description:</h2>
                    <p>{courseOutline.comment_on_results.course_outline.profession_description.description}</p>

                    <h2>Topics:</h2>
                    {courseOutline.comment_on_results.course_outline.topics.map((topic, index) => (
                        <div key={index}>
                            <h3>{topic.title}</h3>
                            <h4>Theory Section:</h4>
                            <p>{topic.theory_section.description}</p>
                            <h4>Recommendations:</h4>
                            <ul>
                                {topic.theory_section.recommendations.recommendation.map((recommendation, idx) => (
                                    <li key={idx}>{recommendation}</li>
                                ))}
                            </ul>
                            <h4>Practical Tasks:</h4>
                            <ul>
                                {topic.practical_tasks.task.map((task, idx) => (
                                    <li key={idx}>{task}</li>
                                ))}
                            </ul>
                        </div>
                    ))}
                    
                    <h2>General Comments:</h2>
                    <p>{courseOutline.comment_on_results.course_outline.general_comments.comment}</p>

                    <h2>Improvement Recommendations:</h2>
                    <p>{courseOutline.comment_on_results.course_outline.general_comments.improvement_recommendations}</p>
                </div>
            )}
        </div>
    );
}
