// UserTestResultsPage.jsx
import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { useUserRoleAccess } from "../Functions/apiUtils";

export default function UserTestResultsPage() {
    const checkUserRoleAccess = useUserRoleAccess(0);
    const [userEmail, setUserEmail] = useState("");
    const [userId, setUserId] = useState("");
    const [userRole, setUserRole] = useState("");
    const [userAnswers, setUserAnswers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    


    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error.message}</div>;
    }

    return (
        <div>
            <div className="container h-100">
                <div className="row h-100">
                    <div className="col-12">
                        <h1>Welcome to this User</h1>
                        <p>User Email: {userEmail}</p>
                        <p>User ID: {userId}</p>
                        <p>User Role: {userRole}</p>
                        {/* Display user answers */}
                        <h2>User Answers</h2>
                        <ul>
                            {userAnswers.map((answer) => (
                                <li key={answer.id}>
                                    Prof Test ID: {answer.prof_test_id}, Question ID: {answer.questions_id}, User Answer: {answer.user_answer}, Correct/Incorrect: {answer.correct_incorrect === 1 ? "Correct" : "Incorrect"}
                                </li>
                            ))}
                        </ul>
                    </div>
                </div>
            </div>
        </div>
    );
}
