// UserTestPage.jsx

import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

export default function UserTestPage() {
    console.error("Error fetching profession details:");
  const { id } = useParams();
  const [profession, setProfession] = useState(null);
  const [testQuestions, setTestQuestions] = useState([]);

  useEffect(() => {
    console.log("Fetching profession details for ID:", id);
    fetch(`http://localhost:5000/get_prof/${id}`)
      .then((response) => response.json())
      .then((data) => {
        console.log("Profession details fetched:", data);
        setProfession(data);
      })
      .catch((error) => console.error("Error fetching profession details:", error));
  
    console.log("Fetching test questions...");
    fetch(`http://localhost:5000/get_test_questions`)
      .then((response) => response.json())
      .then((data) => {
        console.log("Test questions fetched:", data);
        setTestQuestions(data.profTestCreatedQuestions);
      })
      .catch((error) => console.error("Error fetching test questions:", error));
  }, [id]);
  

  return (
    <div>
      <h2>Profession Details</h2>
      {profession && (
        <div>
          <p>Profession ID: {profession.id}</p>
          <p>Profession Name: {profession.profession_name}</p>
          <p>Description: {profession.profession_description}</p>
        </div>
      )}

      <h2>Test Questions</h2>
      <ul>
        {testQuestions.map((question, index) => (
          <li key={question.id}>
            <p>Question {index + 1}: {question.question}</p>
            <p>Correct Answer: {question.correct_answer}</p>
            <p>Answer Variants:</p>
            <ul>
              <li>{question.answer_variant1}</li>
              <li>{question.answer_variant2}</li>
              <li>{question.answer_variant3}</li>
              <li>{question.answer_variant4}</li>
            </ul>
          </li>
        ))}
      </ul>
    </div>
  );
}
