// AdminManageUsersPage.jsx
import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { fetchData, renderListItemsWithAction, deleteItem, updateItem } from "../Functions/apiUtils";

export default function AdminManageUsersPage() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    fetchData("http://localhost:5000/get_users", setUsers, "users");
  }, []);

  const renderUserItem = (user, handleDelete, handleModify) => (
    <div>
      <p>(User email: {user.email}) (Role ID: {user.role_id})</p>
      <button onClick={() => handleDelete(user.id)}>Delete</button>
      <button onClick={() => handleModify(user.id, { /* pass the new data here if needed */ })}>Modify</button>
    </div>
  );
  

  const handleDelete = async (userId) => {
    try {
      await deleteItem("http://localhost:5000/delete_user", userId, handleDeleteCallback);
    } catch (error) {
      console.error("Error deleting user:", error);
    }
  };

  const handleDeleteCallback = (userId) => {
    setUsers(users.filter((user) => user.id !== userId));
  };

  const handleModify = async (userId, newData) => {
    try {
      await updateItem("http://localhost:5000/modify_user", userId, newData, handleModifyCallback);
    } catch (error) {
      console.error("Error modifying user:", error);
    }
  };

  const handleModifyCallback = (userId, newData) => {
    setUsers(users.map((user) => (user.id === userId ? { ...user, ...newData } : user)));
  };

  return (
    <div>
      <div className="container h-100">
        <div className="row h-100">
          <div className="col-12">
            <h1>Welcome to Users Manage</h1>
            <p>
              <Link to="/login" className="btn btn-success">Login</Link> | <Link to="/register" className="btn btn-success">register</Link>
            </p>
            <p>
              <Link to="/admin_users" className="btn btn-success">Manage Users</Link> | <Link to="/admin_prof" className="btn btn-success">Manage professions</Link>
            </p>

            {/* Display Users */}
            <h2>Users List</h2>
            {renderListItemsWithAction(users, renderUserItem, handleDelete, handleModify)}

            {/* Add New User Form (example, modify as needed) */}
            <h2>Add New User</h2>
            <form method="post" action="http://localhost:5000/add_user">
              <label>Email:</label>
              <input type="text" name="email" required />
              <label>Password:</label>
              <input type="password" name="password" required />
              <label>Role ID:</label>
              <input type="number" name="role_id" />
              <button type="submit">Add User</button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}
