// apiUtils.js

export const fetchData = async (url, setData, dataKey) => {
  try {
    const response = await fetch(url);
    const data = await response.json();
    console.log("Fetched data:", data);

    if (Array.isArray(data[dataKey])) {
      setData(data[dataKey]);
    }
  } catch (error) {
    console.error(`Error fetching data from ${url}:`, error);
  }
};

export const renderListItems = (items, renderFunction) => (
  <ul>
    {Array.isArray(items) ? items.map((item) => (
      <li key={item.id}>{renderFunction(item)}</li>
    )) : null}
  </ul>
);

export const renderListItemsWithAction = (items, renderFunction, handleDelete, handleModify) => (
  <ul>
    {Array.isArray(items) ? items.map((item) => (
      <li key={item.id}>
        {renderFunction(item, handleDelete, handleModify)}
      </li>
    )) : null}
  </ul>
);


export const deleteItem = async (url, itemId, callback) => {
  try {
    await fetch(`${url}/${itemId}`, {
      method: "DELETE",
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json',
          "Access-Control-Allow-Origin": "*",
          "Access-Control-Allow-Methods": "*"
        },
        text: "Hello, world."
    });
    callback(itemId);
  } catch (error) {
    console.error(`Error deleting item with ID ${itemId}:`, error);
  }
};

export const updateItem = async (url, itemId, newData, callback) => {
  try {
    await fetch(`${url}/${itemId}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newData),
    });
    callback(itemId, newData);
  } catch (error) {
    console.error(`Error updating item with ID ${itemId}:`, error);
  }
};
