import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Recommendations = ({ resultId }) => {
  const [recommendations, setRecommendations] = useState([]);
  const [userId, setUserId] = useState('');

  useEffect(() => {
    const userIdFromStorage = sessionStorage.getItem("user_id");
    setUserId(userIdFromStorage);

    const fetchRecommendations = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/get_user_xml/${userIdFromStorage}`);
        setRecommendations(response.data.user_xmls);
      } catch (error) {
        console.error('Error fetching XML file:', error);
      }
    };
    fetchRecommendations();
  }, [resultId]);

  return (
    <div>
      {recommendations.map((xml, index) => (
        <div key={index}>
          <h2>{xml.profession_description.name} Course Outline</h2>
          <div>
            <h3>Profession Description:</h3>
            <p>{xml.profession_description.description}</p>
          </div>
          <div>
            <h3>Topics:</h3>
            {xml.topics.map((topic, topicIndex) => (
              <div key={topicIndex}>
                <h3>{topic.title}</h3>
                <div>
                  <h4>Theory Section:</h4>
                  <p>{topic.theory_section.description}</p>
                  <h4>Recommendations:</h4>
                  <ul>
                    {topic.theory_section.recommendations.map((rec, recIndex) => (
                      <li key={recIndex}>{rec}</li>
                    ))}
                  </ul>
                </div>
                <div>
                  <h4>Practical Tasks:</h4>
                  <ul>
                    {topic.practical_tasks.map((task, taskIndex) => (
                      <li key={taskIndex}>{task}</li>
                    ))}
                  </ul>
                </div>
              </div>
            ))}
          </div>
          <div>
            <h3>General Comments:</h3>
            <p>{xml.general_comments.comment}</p>
          </div>
          <div>
            <h3>Improvement Recommendations:</h3>
            <p>{xml.general_comments.improvement_recommendations}</p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Recommendations;
