//UserCoursePage.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Recommendations = ({ resultId }) => {
  const [recommendations, setRecommendations] = useState('');
  const [userId, setUserId] = useState('');

  useEffect(() => {
    const userIdFromStorage = sessionStorage.getItem("user_id");
    setUserId(userIdFromStorage);

    const fetchRecommendations = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/get_user_xml/${userIdFromStorage}`);
        setRecommendations(response.data.user_xmls);
      } catch (error) {
        console.error('Error fetching XML file:', error);
      }
    };
    fetchRecommendations();
  }, [resultId]);

  // Parse XML string and format it for display as plain text
  const formatXmlAsText = (xmlString) => {
    if (!xmlString) return '';
    // Replace '<' and '>' with HTML entities to prevent rendering as HTML
    return xmlString.replace(/</g, '&lt;').replace(/>/g, '&gt;');
  };

  return (
    <div>
      <pre>{formatXmlAsText(recommendations)}</pre>
    </div>
  );
};

export default Recommendations;
