import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

class AdminManagePage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
    };
  }

  componentDidMount() {
    // Fetch data from backend when the component mounts
    fetch(this.props.apiEndpoint)
      .then((response) => response.json())
      .then((data) => {
        if (data[this.props.dataKey]) {
          this.setState({ data: data[this.props.dataKey] });
        }
      })
      .catch((error) => console.error(`Error fetching ${this.props.dataKey}:`, error));
  }

  render() {
    return (
      <div>
        <div className="container h-100">
          <div className="row h-100">
            <div className="col-12">
              <h1>{this.props.pageTitle}</h1>
              <p>
                <p>
                  <Link to="/login" className="btn btn-success">
                    Login
                  </Link>{" "}
                  |{" "}
                  <Link to="/register" className="btn btn-success">
                    Register
                  </Link>{" "}
                </p>
                <p>
                  <Link to="/admin_users" className="btn btn-success">
                    Manage Users
                  </Link>{" "}
                  |{" "}
                  <Link to="/admin_prof" className="btn btn-success">
                    Manage Professions
                  </Link>
                </p>
              </p>

              <h2>{this.props.listTitle}</h2>
              <ul>
                {this.state.data.map((item) => (
                  <li key={item.id}>{this.props.renderItem(item)}</li>
                ))}
              </ul>

              <h2>{this.props.formTitle}</h2>
              {this.props.renderForm()}
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default AdminManagePage;
