// AdminManageUsersPage.jsx
import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { fetchData, renderListItemsWithActions } from "../Functions/apiUtils";

export default function AdminManageUsersPage() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    fetchData("http://localhost:5000/get_users", setUsers, "users");
  }, []);

  const renderUserItemWithActions = (user, deleteHandler, modifyHandler) => (
    <React.Fragment>
      <p>(User email: {user.email}) (Role ID: {user.role_id})</p>
      <button onClick={() => deleteHandler(user.id)}>Delete</button>
      <button onClick={() => modifyHandler(user)}>Modify</button>
    </React.Fragment>
  );

  const deleteUser = (userId) => {
    // Implement your delete logic here
    console.log(`Delete user with ID ${userId}`);
  };

  const modifyUser = (user) => {
    // Implement your modify logic here
    console.log(`Modify user:`, user);
  };

  return (
    <div>
      <div className="container h-100">
        <div className="row h-100">
          <div className="col-12">
            <h1>Welcome to Users Manage</h1>
            <p>
              <Link to="/login" className="btn btn-success">Login</Link> | <Link to="/register" className="btn btn-success">register</Link>
            </p>
            <p>
              <Link to="/admin_users" className="btn btn-success">Manage Users</Link> | <Link to="/admin_prof" className="btn btn-success">Manage professions</Link>
            </p>

            {/* Display Users with Actions */}
            <h2>Users List</h2>
            {renderListItemsWithActions(users, renderUserItemWithActions, deleteUser, modifyUser)}

            {/* Add New User Form (example, modify as needed) */}
            <h2>Add New User</h2>
            <form method="post" action="http://localhost:5000/add_user">
              <label>Email:</label>
              <input type="text" name="email" required />
              <label>Password:</label>
              <input type="password" name="password" required />
              <label>Role ID:</label>
              <input type="number" name="role_id" />
              <button type="submit">Add User</button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}
