import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { getUserInfo } from "../Functions/authUtils";

export default function CourseOutlinePage() {
    const [courseOutline, setCourseOutline] = useState(null);
    const { userId } = getUserInfo();

    useEffect(() => {
        fetchCourseOutline();
    }, []);

    const fetchCourseOutline = () => {
        // Fetch course outline JSON data
        fetch(`http://localhost:5000/get_user_json/${userId}`)
            .then((response) => response.json())
            .then((data) => {
                if (data.user_jsons && data.user_jsons.length > 0) {
                    // Assuming only one course outline for simplicity
                    setCourseOutline(data.user_jsons[0]);
                }
            })
            .catch((error) => console.error("Error fetching course outline:", error));
    };

    return (
        <div>
            <h1>Course Outline</h1>
            <Link to="/user_main_page" className="btn btn-primary">
                Back to Main Page
            </Link>
            <hr />
            {courseOutline && (
                <div className="card my-3">
                    <div className="card-header">
                        <h3>Course Outline</h3>
                    </div>
                    <div className="card-body">
                        <p>
                            <strong>Comment on Results:</strong> {courseOutline.comment_on_results}
                        </p>
                        <div>
                            <h4>Topics:</h4>
                            {courseOutline.course_outline.topics.map((topic, index) => (
                                <div key={index}>
                                    <h5>{topic.title}</h5>
                                    <p><strong>Description:</strong> {topic.theory_section.description}</p>
                                    <h6>Recommendations:</h6>
                                    <ul>
                                        {topic.theory_section.recommendations.recommendation.map((recommendation, idx) => (
                                            <li key={idx}>{recommendation}</li>
                                        ))}
                                    </ul>
                                    <h6>Practical Tasks:</h6>
                                    <ul>
                                        {topic.practical_tasks.task.map((task, idx) => (
                                            <li key={idx}>{task}</li>
                                        ))}
                                    </ul>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}
