import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

export default function UserTestPage() {
  const { id } = useParams();
  const [profession, setProfession] = useState(null);
  const [testQuestions, setTestQuestions] = useState([]);

  useEffect(() => {
    console.log("Fetching profession details for ID:", id);
    fetch(`http://localhost:5000/get_prof/${id}`)
      .then((response) => response.json())
      .then((data) => {
        console.log("Profession details fetched:", data);
        setProfession(data.profession);
      })
      .catch((error) => console.error("Error fetching profession details:", error));

    console.log("Fetching test questions for profession ID:", id);
    fetch(`http://localhost:5000/get_test_questions_by_profession/${id}`)
      .then((response) => response.json())
      .then((data) => {
        console.log("Test questions fetched:", data);
        setTestQuestions(data.testQuestions);
      })
      .catch((error) => console.error("Error fetching test questions:", error));
  }, [id]);

  const handleSubmit = (event) => {
    event.preventDefault();
    // You can implement submission logic here, e.g., checking answers
    console.log("Form submitted!");
  };

  return (
    <div>
        <h2>Profession Details</h2>
        {profession && (
        <div>
            <p>Profession ID: {profession.id}</p>
            <p>Profession Name: {profession.profession_name}</p>
            <p>Description: {profession.profession_description}</p>
        </div>
        )}


        <h2>Test Questions</h2>
        <form onSubmit={handleSubmit}>
        {testQuestions.length > 0 && testQuestions.map((questionData, index) => (
            <div key={index}>
                <p>Question {index + 1}: {questionData.question}</p>
                <div>
                    {['answer_variant1', 'answer_variant2', 'answer_variant3', 'answer_variant4'].map((variant, i) => (
                        <div key={i}>
                            <input type="radio" id={`${index}_${i}`} name={`question_${index}`} value={questionData[variant]} />
                            <label htmlFor={`${index}_${i}`}>{questionData[variant]}</label>
                        </div>
                    ))}
                </div>
            </div>
        ))}


        <button type="submit">Submit Answers</button>
        </form>
    </div>
    );

}
