// UserCoursePage.jsx
import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { getUserInfo } from "../Functions/authUtils";

export default function UserCoursePage() {
    const [userJsons, setUserJsons] = useState([]);
    const { userId } = getUserInfo();

    useEffect(() => {
        fetchUserJsons();
    }, []);

    const fetchUserJsons = () => {
        fetch(`http://localhost:5000/get_user_json/${userId}`)
            .then((response) => response.json())
            .then((data) => {
                if (data.user_jsons) {
                    setUserJsons(data.user_jsons);
                }
            })
            .catch((error) => console.error("Error fetching user JSONs:", error));
    };

    return (
        <div>
            <h1>User Course Page</h1>
            <Link to="/user_main_page" className="btn btn-primary">
                Back to Main Page
            </Link>
            <hr />
            {userJsons.map((json, index) => (
                <div key={index} className="card my-3">
                    <div className="card-header">
                        {json.course_outline && json.course_outline.profession_description && (
                            <>
                                <h3>{json.course_outline.profession_description.name}</h3>
                                <p>{json.course_outline.profession_description.description}</p>
                            </>
                        )}
                    </div>
                    <div className="card-body">
                        <h4>Topics:</h4>
                        {json.course_outline && json.course_outline.topics && json.course_outline.topics.map((topic, idx) => (
                            <div key={idx} className="my-3">
                                <h5>{topic.title}</h5>
                                <p><strong>Theory Section:</strong> {topic.theory_section.description}</p>
                                <p><strong>Recommendations:</strong></p>
                                <ul>
                                    {topic.theory_section.recommendations.recommendation.map((item, i) => (
                                        <li key={i}>{item}</li>
                                    ))}
                                </ul>
                                <p><strong>Practical Tasks:</strong></p>
                                <ul>
                                    {topic.practical_tasks.task.map((task, i) => (
                                        <li key={i}>{task}</li>
                                    ))}
                                </ul>
                            </div>
                        ))}
                        {json.course_outline && json.course_outline.general_comments && (
                            <div className="my-3">
                                <h4>General Comments:</h4>
                                <p><strong>Comment:</strong> {json.course_outline.general_comments.comment}</p>
                                <p><strong>Improvement Recommendations:</strong> {json.course_outline.general_comments.improvement_recommendations}</p>
                            </div>
                        )}
                    </div>
                </div>
            ))}
        </div>
    );
}
