// UserTestPage.jsx
import React, { useState, useEffect } from "react";
import { useParams, useLocation } from "react-router";

import { useUserRoleAccess } from "../Functions/apiUtils";

export default function UserTestPage() {
  const { id } = useParams();
  const [userId, setUserId] = useState("");
  const [userEmail, setUserEmail] = useState("");
  const [userRole, setUserRole] = useState("");
  const [profession, setProfession] = useState(null);
  const [testQuestions, setTestQuestions] = useState([]);
  const [answers, setAnswers] = useState({});
  const [mark, setMark] = useState(null);
  const [correctAnswersCount, setCorrectAnswersCount] = useState(0);
  const [submitted, setSubmitted] = useState(false);
  const [loading, setLoading] = useState(true); // Track loading state
  const [error, setError] = useState(null); // Track error state
  const location = useLocation();
  const userData = location.state || {}; // Extract user data from location state
  const { email, id: userIdFromLocation, role } = userData;
  const checkUserRoleAccess = useUserRoleAccess(0);

  useEffect(() => {
    if (!checkUserRoleAccess()) {
      return;
    }

    const userEmailFromStorage = sessionStorage.getItem("user_email");
    const userIdFromStorage = sessionStorage.getItem("user_id");
    const userRoleFromStorage = sessionStorage.getItem("user_role");


    if (userEmailFromStorage && userIdFromStorage && userRoleFromStorage) {

        setUserEmail(userEmailFromStorage);
        setUserId(userIdFromStorage);
        setUserRole(userRoleFromStorage);

    } else {
      setError("User data not found");
      setLoading(false);
      return;
    }

    // Fetch data if it hasn't been fetched already
    if (!profession || testQuestions.length === 0) {
      Promise.all([
        fetch(`http://localhost:5000/get_prof/${id}`).then((response) => response.json()),
        fetch(`http://localhost:5000/get_test_questions_by_profession/${id}`).then((response) => response.json())
      ]).then(([professionData, testQuestionsData]) => {
        setProfession(professionData.profession);
        setTestQuestions(testQuestionsData);
        setLoading(false);
      }).catch((error) => {
        setError("Error fetching data. Please try again later.");
        setLoading(false);
      });
    } else {
      setLoading(false);
    }
  }, [id, userIdFromLocation, email, role, checkUserRoleAccess, profession, testQuestions]);

  const handleAnswerChange = (questionIndex, selectedAnswer) => {
    setAnswers({
      ...answers,
      [questionIndex]: selectedAnswer
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    let correctAnswers = 0;
  
    testQuestions.forEach((question, index) => {
      if (answers[index] === question.correct_answer) {
        correctAnswers++;
      }
    });
  
    const calculatedMark = Math.round((correctAnswers / testQuestions.length) * 100);
  
    const testData = {
      prof_test_id: id,
      user_id: userId,
      mark: calculatedMark,
      correct_answers_amount: correctAnswers,
      incorrect_answers_amount: testQuestions.length - correctAnswers,
      // Remove the 'comment_on_results' property from testData
    };
  
    fetch("http://localhost:5000/save_test_results", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(testData)
    })
    .then(response => response.json())
    .then(data => {
      console.log(data.message);
      // If the test results are saved successfully, you can update the state
      // to indicate that the submission is completed
      setMark(calculatedMark);
      setCorrectAnswersCount(correctAnswers);
      setSubmitted(true);
    })
    .catch(error => {
      console.error("Error saving test results:", error);
    });
  };
  

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      <h1>Welcome to this User, {userEmail}</h1>
      <p>User ID: {userId}</p>
      <p>User Role: {userRole}</p>
      {profession && (
        <div>
          <h2>Profession Details</h2>
          <p>Profession Name: {profession.profession_name}</p>
          <p>Description: {profession.profession_description}</p>
        </div>
      )}

      <h2>Test Questions</h2>
      <form onSubmit={handleSubmit}>
        {testQuestions.map((question, index) => (
          <div key={index}>
            <p>{index + 1}. {question.question} Level: {question.level_of_question}</p>
            <ul>
              {Object.values(question).slice(0, 4).map((option, optionIndex) => (
                <li key={optionIndex}>
                  <label style={{ color: submitted && option === question.correct_answer ? 'green' : (submitted && answers[index] !== option ? 'red' : 'inherit') }}>
                    <input 
                      type="radio" 
                      name={`question${index}`} 
                      value={option} 
                      checked={answers[index] === option} 
                      onChange={() => handleAnswerChange(index, option)} 
                      disabled={submitted} // Disable input after submission
                    />
                    {option}
                  </label>
                  {submitted && answers[index] !== option && option === question.correct_answer && (
                    <span style={{ color: 'green' }}> ✓</span>
                  )}
                  {submitted && answers[index] === option && option !== question.correct_answer && (
                    <span style={{ color: 'red' }}> x</span>
                  )}
                </li>
              ))}
            </ul>
          </div>
        ))}
        <button type="submit" disabled={submitted}>Submit</button>
      </form>

      {mark !== null && (
        <div>
          <h2>Test Result</h2>
          <p>Mark: {mark}%</p>
          <p>Correct Answers: {correctAnswersCount}/{testQuestions.length}</p>
        </div>
      )}
    </div>
  );
}
