import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

export default function AdminManageUsersPage() {
  const [professions, setProfessions] = useState([]);

  useEffect(() => {
    // Fetch professions from backend when the component mounts
    fetch("http://localhost:5000/get_prof")
      .then((response) => response.json())
      .then((data) => {
        if (data.prof) {
          setProfessions(data.prof);
        }
      })
      .catch((error) => console.error("Error fetching professions:", error));
  }, []); // Empty dependency array means this effect runs once when component mounts

  const handleAddProfession = (event) => {
    event.preventDefault();

    const form = event.target;
    const formData = new FormData(form);

    fetch("http://localhost:5000/add_prof", {
      method: "POST",
      body: formData,
    })
      .then((response) => response.json())
      .then((data) => {
        console.log(data.message);
        // Refresh the professions list after adding a new profession
        fetch("http://localhost:5000/get_prof")
          .then((response) => response.json())
          .then((data) => {
            if (data.prof) {
              setProfessions(data.prof);
            }
          })
          .catch((error) => console.error("Error fetching professions:", error));
      })
      .catch((error) => console.error("Error adding profession:", error));
  };

  return (
    <div>
      <div className="container h-100">
        <div className="row h-100">
          <div className="col-12">
            <h1>Welcome to Users and Professions Manage</h1>
            <p>
            <p><Link to="/login" className="btn btn-success">Login</Link> | <Link to="/register" className="btn btn-success">register</Link> </p>
                    <p><Link to="/admin_users" className="btn btn-success">Manage Users</Link> | <Link to="/admin_prof" className="btn btn-success">Manage professions</Link></p>
            </p>

            {/* Display Professions */}
            <h2>Professions List</h2>
            <ul>
              {professions.map((prof) => (
                <li key={prof.id}>
                  (Profession Name: {prof.name}) (Description: {prof.description})
                </li>
              ))}
            </ul>

            {/* Add New Profession Form */}
            <h2>Add New Profession</h2>
            <form onSubmit={handleAddProfession}>
              <label>Profession Name:</label>
              <input type="text" name="profession_name" required />
              <label>Description:</label>
              <input type="text" name="profession_description" />
              <button type="submit">Add Profession</button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}
