//UserCoursePage.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Recommendations = ({ resultId }) => {
  const [courseOutline, setCourseOutline] = useState(null);
  const [userId, setUserId] = useState('');

  useEffect(() => {
    const userIdFromStorage = sessionStorage.getItem("user_id");
    setUserId(userIdFromStorage);

    const fetchCourseOutline = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/get_user_xml/${userIdFromStorage}`);
        setCourseOutline(response.data.course_outline);
      } catch (error) {
        console.error('Error fetching course outline:', error);
      }
    };
    fetchCourseOutline();
  }, [resultId]);

  const renderOutline = () => {
    if (!courseOutline) return null;

    return (
      <div>
        <h1>Java Developer Course Outline</h1>
        <h2>Profession Description:</h2>
        <p>{courseOutline.profession_description.description}</p>
        <h2>Topics:</h2>
        {courseOutline.topics.map(topic => (
          <div key={topic.title}>
            <h3>{topic.title}</h3>
            <h4>Theory Section:</h4>
            <p>{topic.theory_section.description}</p>
            <h4>Recommendations:</h4>
            <ul>
              {topic.theory_section.recommendations.recommendation.map((recommendation, index) => (
                <li key={index}>{recommendation}</li>
              ))}
            </ul>
            <h4>Practical Tasks:</h4>
            <ul>
              {topic.practical_tasks.task.map((task, index) => (
                <li key={index}>{task}</li>
              ))}
            </ul>
          </div>
        ))}
        <h2>General Comments:</h2>
        <p>{courseOutline.general_comments.comment}</p>
        <h2>Improvement Recommendations:</h2>
        <p>{courseOutline.general_comments.improvement_recommendations}</p>
      </div>
    );
  };

  return (
    <div>
      {renderOutline()}
    </div>
  );
};

export default Recommendations;
