// UserMainPage.jsx
import React, { useState, useEffect } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";

export default function UserMainPage() {
    const location = useLocation();
    const navigate = useNavigate();
    const { email, id, role } = location.state || {};

    const [professions, setProfessions] = useState([]);

    useEffect(() => {
      // Check if user is authenticated
      const isLoggedIn = sessionStorage.getItem("user_id");
      if (!isLoggedIn) {
          navigate("/login");
      }
  }, [navigate]);

    useEffect(() => {
        fetch("http://localhost:5000/get_prof")
            .then((response) => response.json())
            .then((data) => {
                if (data.professions) {
                    setProfessions(data.professions);
                }
            })
            .catch((error) => console.error("Error fetching professions:", error));
    }, []);

    return (
        <div>
            <div className="container h-100">
                <div className="row h-100">
                    <div className="col-12">
                        <h1>Welcome to this User, {email}</h1>
                        <p>User ID: {id}</p>
                        <p>User Role: {role}</p>
                        <p>
                            <Link to="/login" className="btn btn-success">
                                Login
                            </Link>{" "}
                            |{" "}
                            <Link to="/logout" className="btn btn-success">
                                Logout
                            </Link>{" "}
                            |{" "}
                            <Link to="/register" className="btn btn-success">
                                Register
                            </Link>{" "}
                        </p>
                        {/* Display Professions */}
                        <h2>Professions List</h2>
                        <ul>
                            {professions.map((prof) => (
                                <li key={prof.id}>
                                    (Profession ID: {prof.id}) (Profession Name: {prof.profession_name}) (Description: {prof.profession_description})
                                    <Link to={`/profession_test/${prof.id}`} className="btn btn-info">
                                        Details
                                    </Link>
                                </li>
                            ))}
                        </ul>
                    </div>
                </div>
            </div>
        </div>
    );
}
