// UserCoursePage.jsx
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { getUserInfo } from '../Functions/authUtils';

const UserCoursePage = () => {
    const { userId } = getUserInfo();
    const [userCourses, setUserCourses] = useState([]);

    useEffect(() => {
        const fetchUserCourses = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/get_user_courses/${userId}`);
                setUserCourses(response.data.user_courses);
            } catch (error) {
                console.error('Error fetching user courses:', error);
            }
        };

        fetchUserCourses();
    }, [userId]);

    return (
        <div>
            <div className="container h-100">
                <div className="row h-100">
                    <div className="col-12">
                        <p>
                            <Link to="/logout" className="btn btn-success">Logout</Link> 
                            | <Link to="/user_test_results" className="btn btn-success">Test results</Link> 
                            | <Link to="/user_courses" className="btn btn-success">Your courses</Link>
                        </p>
                        <h1>Your Courses</h1>
                        <ul>
                            {userCourses.map((course, index) => (
                                <li key={index}>
                                    Course Name: {course.name} | Course ID: {course.id} | Course Description: {course.description}
                                    {/* Add more details or actions related to each course if needed */}
                                </li>
                            ))}
                        </ul>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default UserCoursePage;
