import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { useUserRoleAccess } from "../Functions/apiUtils";

export default function UserTestResultsPage() {
    const checkUserRoleAccess = useUserRoleAccess(0);
    const [userEmail, setUserEmail] = useState("");
    const [userId, setUserId] = useState("");
    const [userRole, setUserRole] = useState("");
    const [userAnswers, setUserAnswers] = useState([]);
    
    useEffect(() => {
        // Fetch user information from session storage or state
        const userEmailFromStorage = sessionStorage.getItem("user_email");
        const userIdFromStorage = sessionStorage.getItem("user_id");
        const userRoleFromStorage = sessionStorage.getItem("user_role");

        setUserEmail(userEmailFromStorage);
        setUserId(userIdFromStorage);
        setUserRole(userRoleFromStorage);

        if (!checkUserRoleAccess()) {
            return;
        }

        // Fetch user answers from the backend API
        fetch(`http://localhost:5000/user_answers/${userId}`)
            .then((response) => response.json())
            .then((data) => {
                setUserAnswers(data.userAnswers);
            })
            .catch((error) => console.error("Error fetching user answers:", error));
    }, [userId, checkUserRoleAccess]); // Dependency array including userId and checkUserRoleAccess

    return (
        <div>
            <div className="container h-100">
                <div className="row h-100">
                    <div className="col-12">
                        <h1>Welcome to this User</h1>
                        <p>User Email: {userEmail}</p>
                        <p>User ID: {userId}</p>
                        <p>User Role: {userRole}</p>
                        {/* Display user answers */}
                        <h2>User Answers</h2>
                        <ul>
                            {userAnswers.map((answer) => (
                                <li key={answer.id}>
                                    Prof Test ID: {answer.prof_test_id}, Question ID: {answer.questions_id}, User Answer: {answer.user_answer}, Correct/Incorrect: {answer.correct_incorrect === 1 ? "Correct" : "Incorrect"}
                                </li>
                            ))}
                        </ul>
                    </div>
                </div>
            </div>
        </div>
    );
}
