// UserCoursePage.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import XmlViewer from 'react-xml-viewer';
import { getUserInfo } from "../Functions/authUtils";

const Recommendations = () => {
    const { userId } = getUserInfo();
    const [xmlFiles, setXmlFiles] = useState([]);
    const [selectedFile, setSelectedFile] = useState(null);

    useEffect(() => {
        const fetchXmlFiles = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/get_user_xml/${userId}`);
                console.error('ID:', userId);
                setXmlFiles(response.data.user_xmls);
            } catch (error) {
                console.error('Error fetching XML files:', error +""+ userId);
            }
        };
        fetchXmlFiles();
    }, [userId]);

    const handleFileSelect = (fileName) => {
        const file = xmlFiles.find(file => file.name === fileName);
        setSelectedFile(file);
    };
    

    return (
        <div>
            <label htmlFor="file-select">Select XML File:</label>
            <select id="file-select" onChange={(e) => handleFileSelect(e.target.value)}>
                <option value="">Choose a file...</option>
                {xmlFiles.map((file, index) => (
                    <option key={index} value={file.name}>{file.name}</option>
                ))}
            </select>
            {selectedFile && (
                <XmlViewer xml={selectedFile} />
            )}
        </div>
    );
};

export default Recommendations;
