// UserTestResultsPage.jsx
import { useUserRoleAccess } from "../Functions/apiUtils";
import React, { useState, useEffect, useRef } from "react";
import { Link } from "react-router-dom";

// Arrow icons for indicating collapse/expand state
const arrowDown = "\u25BC";
const arrowUp = "\u25B2";

export default function UserTestResultsPage() {
    const [userEmail, setUserEmail] = useState("");
    const [userId, setUserId] = useState("");
    const [userRole, setUserRole] = useState("");
    const [userAnswers, setUserAnswers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [collapsedTests, setCollapsedTests] = useState({});
    const [fetchedInfo, setFetchedInfo] = useState(null); // New state for fetched info

    // Define a ref to track previous userId
    const previousUserId = useRef(null);

    useEffect(() => {
        // Fetch user information from session storage or state
        const userEmailFromStorage = sessionStorage.getItem("user_email");
        const userIdFromStorage = sessionStorage.getItem("user_id");
        const userRoleFromStorage = sessionStorage.getItem("user_role");

        setUserEmail(userEmailFromStorage);
        setUserId(userIdFromStorage);
        setUserRole(userRoleFromStorage);

        if (!userId) {
            setLoading(false);
            return;
        }

        // Fetch user answers when userId changes
        fetchUserAnswers(userId);
    }, [userId]); // Trigger effect when userId changes

    // Function to fetch user answers from the backend
    const fetchUserAnswers = async (userId) => {
        try {
            const response = await fetch(`/user_answers/${userId}`);
            if (!response.ok) {
                throw new Error("Failed to fetch user answers");
            }
            const data = await response.json();
            setUserAnswers(data.userAnswers);
            setLoading(false);
        } catch (error) {
            setError(error.message);
            setLoading(false);
        }
    };

    // Loading and error handling
    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    // Render user answers
    return (
        <div>
            <div className="container h-100">
                <div className="row h-100">
                    <div className="col-12">
                        <h1>Welcome to this User</h1>
                        <p>User Email: {userEmail}</p>
                        <p>User ID: {userId}</p>
                        <p>User Role: {userRole}</p>

                        <p>
                            <Link to="/logout" className="btn btn-success">Logout</Link> 
                            | <Link to="/user_main" className="btn btn-success">Main</Link> 
                        </p>

                        {/* Display user answers */}
                        <h2>User Answers:</h2>
                        {Object.keys(userAnswers).map(professionId => (
                            <div key={professionId}>
                                <h3>{`Profession ID: ${professionId}`}</h3>
                                {userAnswers[professionId].map(answer => (
                                    <div key={answer.id}>
                                        <p>Question: {answer.question_text}</p>
                                        <p>User Answer: {answer.user_answer}</p>
                                        <p>Correct/Incorrect: {answer.correct_incorrect ? 'Correct' : 'Incorrect'}</p>
                                    </div>
                                ))}
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    );
}
