// apiUtils.js
export const fetchData = async (url, setData, dataKey) => {
  try {
    const response = await fetch(url);
    const data = await response.json();
    console.log("Fetched data:", data);

    if (Array.isArray(data[dataKey])) {
      setData(data[dataKey]);
    }
  } catch (error) {
    console.error(`Error fetching data from ${url}:`, error);
  }
};

export const renderListItems = (items, renderFunction) => (
  <ul>
    {Array.isArray(items) ? items.map((item) => (
      <li key={item.id}>{renderFunction(item)}</li>
    )) : null}
  </ul>
);

export const renderListItemsWithActions = (items, renderFunction, onDelete, onModify) => (
  <ul>
    {Array.isArray(items) ? items.map((item) => (
      <li key={item.id}>
        {renderFunction(item)}
        <button onClick={() => onDelete(item.id)}>Delete</button>
        <button onClick={() => onModify(item)}>Modify</button>
      </li>
    )) : null}
  </ul>
);

export const deleteItem = async (url, itemId, setItems, items, key) => {
  try {
    const response = await fetch(`${url}/${itemId}`, {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json",
      },
    });

    if (response.ok) {
      const updatedItems = items.filter((item) => item.id !== itemId);
      setItems(updatedItems);
    } else {
      console.error(`Failed to delete item with ID ${itemId}`);
    }
  } catch (error) {
    console.error(`Error deleting item with ID ${itemId}:`, error);
  }
};

export const modifyItem = async (url, modifiedItem, setItems, items, key) => {
  try {
    const response = await fetch(`${url}/${modifiedItem.id}`, {
      method: "PUT", // Use "PUT" or "PATCH" depending on your API
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(modifiedItem),
    });

    if (response.ok) {
      const updatedItems = items.map((item) =>
        item.id === modifiedItem.id ? modifiedItem : item
      );
      setItems(updatedItems);
    } else {
      console.error(`Failed to modify item with ID ${modifiedItem.id}`);
    }
  } catch (error) {
    console.error(`Error modifying item with ID ${modifiedItem.id}:`, error);
  }
};
