//UserCoursePage.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import XmlViewer from 'react-xml-viewer'; // Change import to default

const Recommendations = ({ resultId }) => {
  const [recommendations, setRecommendations] = useState('');
  const [userId, setUserId] = useState('');

  useEffect(() => {
    const userIdFromStorage = sessionStorage.getItem("user_id");
    setUserId(userIdFromStorage);

    const fetchRecommendations = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/get_user_xml/${userIdFromStorage}`);
        setRecommendations(response.data.user_xmls);
      } catch (error) {
        console.error('Error fetching XML file:', error);
      }
    };
    fetchRecommendations();
  }, [resultId]);

  // Function to parse XML and render profession description
  const renderProfessionDescription = () => {
    if (!recommendations) return null;

    const parser = new DOMParser();
    const xmlDoc = parser.parseFromString(recommendations, 'text/xml');
    const professionNode = xmlDoc.querySelector('profession_description');

    if (!professionNode) return null;

    const name = professionNode.querySelector('name').textContent;
    const description = professionNode.querySelector('description').textContent;

    return (
      <div>
        <h3>Profession Description</h3>
        <p><strong>Name:</strong> {name}</p>
        <p><strong>Description:</strong> {description}</p>
      </div>
    );
  };

  return (
    <div>
      <h2>Recommendations</h2>
      {renderProfessionDescription()}
      <XmlViewer xml={recommendations} />
    </div>
  );
};

export default Recommendations;
