import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { fetchData, renderListItems } from "./Functions/apiUtils";
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import { Breadcrumb, Layout, Menu, theme } from 'antd';

export default function LandingPage(){
  const [professions, setProfessions] = useState([]);

  useEffect(() => {
    fetchData("http://localhost:5000/get_prof", setProfessions, "professions");
  }, []);

  const renderProfItem = (prof) => (
    <Card key={prof.id} style={{ width: '18rem', marginBottom: '20px' }}>
      <Card.Img variant="top" src="holder.js/100px180" />
      <Card.Body>
        <Card.Title>{prof.profession_name}</Card.Title>
        <Card.Text>{prof.profession_description}</Card.Text>
        <Link to={`/profession/${prof.id}`} className="btn btn-primary">View Details</Link>
      </Card.Body>
    </Card>
  );

  const { Header, Content, Footer } = Layout;

  const items = new Array(15).fill(null).map((_, index) => ({
    key: index + 1,
    label: `nav ${index + 1}`,
  }));

  const {
    token: { colorBgContainer, borderRadiusLG },
  } = theme.useToken();

  return (
        <Layout>
        <Header style={{ display: 'flex', alignItems: 'center' }}>
          <div className="demo-logo" />
          <Menu
            theme="dark"
            mode="horizontal"
            defaultSelectedKeys={['2']}
            items={items}
            style={{ flex: 1, minWidth: 0 }}
          />
        </Header>
        <Content style={{ padding: '0 48px' }}>
          <Breadcrumb style={{ margin: '16px 0' }}>
            <Breadcrumb.Item>Home</Breadcrumb.Item>
            <Breadcrumb.Item>List</Breadcrumb.Item>
            <Breadcrumb.Item>App</Breadcrumb.Item>
          </Breadcrumb>
          <div
            style={{
              background: colorBgContainer,
              minHeight: 280,
              padding: 24,
              borderRadius: borderRadiusLG,
            }}
          >
                      <div>
          <div className="container h-100">
            <div className="row h-100">
              <div className="col-12">
                <h1>Welcome to this React Application</h1>
                <p>
                  <Link to="/login" className="btn btn-success">Login</Link> | 
                  <Link to="/register" className="btn btn-success">Register</Link>
                </p>
              
                {/* Display Professions */}
                <h2>Professions List</h2>
                {renderListItems(professions, renderProfItem)}
              </div>
            </div>
          </div>
        </div>
          </div>

        </Content>
        <Footer style={{ textAlign: 'center' }}>
          EdySystem ©{new Date().getFullYear()} Created by KirilsK
        </Footer>
      </Layout>
  );
}
