// UserCoursePage.jsx
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { getUserInfo } from '../Functions/authUtils';
import { useUserRoleAccess } from "../Functions/apiUtils";

const UserCoursePage = () => {
    const { userId } = getUserInfo();
    const [userCourses, setUserCourses] = useState([]);
    const [userXml, setUserXml] = useState(''); // State to store XML content
    const checkUserRoleAccess = useUserRoleAccess(0);

    useEffect(() => {
        // Fetch XML data for the user when the component mounts
        fetchUserXml();
    }, []);

    const fetchUserXml = async () => {
        try {
            const response = await axios.get(`http://localhost:5000/get_user_xml/${userId}`);
            setUserXml(response.data.user_xml); // Assuming the response contains XML data in 'user_xml' field
        } catch (error) {
            console.error('Error fetching user XML:', error);
        }
    };

    return (
        <div>
            <div className="container h-100">
                <div className="row h-100">
                    <div className="col-12">
                        <p>
                            <Link to="/logout" className="btn btn-success">Logout</Link> 
                            | <Link to="/user_test_results" className="btn btn-success">Test results</Link> 
                            | <Link to="/user_courses" className="btn btn-success">Your courses</Link>
                        </p>
                        <h1>Your Courses</h1>
                        <ul>
                            {userCourses.map((course, index) => (
                                <li key={index}>
                                    Course Name: {course.name} | Course ID: {course.id} | Course Description: {course.description}
                                    {/* Add more details or actions related to each course if needed */}
                                </li>
                            ))}
                        </ul>
                    </div>
                </div>
            </div>
            {/* Display XML content */}
            <div>
                <h2>User XML:</h2>
                <pre>{userXml}</pre>
            </div>
        </div>
    );
};

export default UserCoursePage;
