import React, { useState } from 'react';
import { Link } from 'react-router-dom';

import logo from '../logo.jpg';

import './Navbar.css';
import { Button } from './Button';

function Navbar() {
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);


  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const showButton = () => {
    if(window.innerWidth <= 960){
      setButton(false)
    } else{
      setButton(true)
    }
  };

  window.addEventListener('resize', showButton)

  return (
    <>
        <nav className="navbar">
          <div className="navbar-container">
            <Link to='/' className="navbar-logo">
              <img src={logo} alt="Logo" ></img>
            </Link>
            <div className='menu-icon' onClick={handleClick}>
              <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
            </div>
            <ul className={click ? 'nav-menu active' : 'nav-menu'}>
              <li className='nav-item'>
                <Link to='/ideja' className='nav-links' onClick={closeMobileMenu}>
                  Ideja
                </Link>
              </li>
              <li className='nav-item'>
                <Link to='/piesaki-sapni' className='nav-links' onClick={closeMobileMenu}>
                  Piesaki Sapni
                </Link>
              </li>
              <li className='nav-item'>
                <Link to='/uznemejiem' className='nav-links' onClick={closeMobileMenu}>
                  Uzņēmējiem
                </Link>
              </li>
              <li className='nav-item'>
                <Link to='/sapnu-banka' className='nav-links' onClick={closeMobileMenu}>
                  Sapņu banka
                </Link>
              </li>
              <li className='nav-item'>
                <Link to='/sapnus-atbalsta' className='nav-links' onClick={closeMobileMenu}>
                  Sapņus atbalsta
                </Link>
              </li>
              <li className='nav-item'>
                <Link to='/99-iedvesmas-stasti' className='nav-links' onClick={closeMobileMenu}>
                  99 Iedvesmas stāsti
                </Link>
              </li>
              <li className='nav-item'>
                <Link to='/LV' className='nav-links' id='LV' onClick={closeMobileMenu}>
                  LV
                </Link>
              </li>
              
            </ul>

          </div>
        </nav>
    </>
  )
}

//{button && <Button buttonStyle='btn--primary' buttonSize='.btn--medium'>Sign Up</Button> }


export default Navbar
