import React, { useEffect, useState } from 'react';
import axios from 'axios';

const CoursePage = () => {
  const [recommendations, setRecommendations] = useState([]);
  const [userId, setUserId] = useState('');

  useEffect(() => {
    const userIdFromStorage = sessionStorage.getItem("user_id");
    setUserId(userIdFromStorage);

    const fetchRecommendations = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/get_user_json/${userIdFromStorage}`);
        setRecommendations(response.data.user_jsons);
      } catch (error) {
        console.error('Error fetching json files:', error);
      }
    };
    fetchRecommendations();
  }, []);

  const formatRecommendation = (recommendation) => {
    if (!recommendation || !recommendation.course || !recommendation.topics) {
      return null; // Skip rendering if recommendation data is incomplete
    }

    return (
      <div key={recommendation.course}>
        <h2>{recommendation.course}</h2>
        <p><strong>Profession Description:</strong> {recommendation.profession_description}</p>
        <h3>Topics:</h3>
        {recommendation.topics.map((topic, index) => (
          <div key={index}>
            <h4>{topic.title}</h4>
            <p><strong>Theory Section:</strong> {topic.theory_section}</p>
            <p><strong>Recommendations:</strong> {topic.recommendations.join(', ')}</p>
            <p><strong>Practical Tasks:</strong></p>
            <ul>
              {topic.practical_tasks.map((task, idx) => (
                <li key={idx}>{task}</li>
              ))}
            </ul>
          </div>
        ))}
        <p><strong>General Comments:</strong> {recommendation.general_comments}</p>
        <p><strong>Improvement Recommendations:</strong> {recommendation.improvement_recommendations}</p>
      </div>
    );
  };

  return (
    <div>
      <h1>Course Page</h1>
      {recommendations.map((recommendation, index) => (
        formatRecommendation(recommendation)
      ))}
    </div>
  );
};

export default CoursePage;
