import React, { useState, useEffect } from "react";
import { useParams, useLocation } from "react-router-dom";

export default function UserTestPage() {
  const { id } = useParams();
  const [userId, setUserId] = useState(""); // State to store user ID
  const [userEmail, setUserEmail] = useState(""); // State to store user email
  const [userRole, setUserRole] = useState(""); // State to store user role
  const [profession, setProfession] = useState(null);
  const [testQuestions, setTestQuestions] = useState([]);
  const [answers, setAnswers] = useState({});
  const [mark, setMark] = useState(null);
  const [correctAnswersCount, setCorrectAnswersCount] = useState(0);
  const [submitted, setSubmitted] = useState(false); // Track if form has been submitted

  const location = useLocation();
  const { email, id: userIdFromLocation, role } = location.state || {}; // Extract user data from location state

  useEffect(() => {
    // Fetch profession details
    fetch(`http://localhost:5000/get_prof/${id}`)
      .then((response) => response.json())
      .then((data) => {
        setProfession(data.profession);
      })
      .catch((error) => console.error("Error fetching profession details:", error));

    // Fetch test questions
    fetch(`http://localhost:5000/get_test_questions_by_profession/${id}`)
      .then((response) => response.json())
      .then((data) => {
        setTestQuestions(data);
      })
      .catch((error) => console.error("Error fetching test questions:", error));

    // Set userId and userEmail
    setUserId(userIdFromLocation);
    setUserEmail(email);
    setUserRole(role);
  }, [id, userIdFromLocation, email, role]);

  // Function to handle radio button change
  const handleAnswerChange = (questionIndex, selectedAnswer) => {
    setAnswers({
      ...answers,
      [questionIndex]: selectedAnswer
    });
  };

  // Function to handle form submission
  const handleSubmit = (event) => {
    event.preventDefault();
    let correctAnswers = 0;

    // Count correct answers
    testQuestions.forEach((question, index) => {
      if (answers[index] === question.correct_answer) {
        correctAnswers++;
      }
    });

    const calculatedMark = Math.round((correctAnswers / testQuestions.length) * 100);

    // Prepare data to send to backend
    const testData = {
      prof_test_id: id,
      user_id: userId,
      mark: calculatedMark,
      correct_answers_amount: correctAnswers,
      incorrect_answers_amount: testQuestions.length - correctAnswers,
      comment_on_results: "Any comment you want to add"
    };

    // Send test results to backend
    fetch("http://localhost:5000/save_test_results", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(testData)
    })
    .then(response => response.json())
    .then(data => {
      console.log(data.message); // Handle success message
    })
    .catch(error => {
      console.error("Error saving test results:", error);
    });

    // Update state
    setMark(calculatedMark);
    setCorrectAnswersCount(correctAnswers);
    setSubmitted(true); // Set submitted to true after form submission
  };

  return (
    <div>
      <h1>Welcome to this User, {userEmail}</h1>
      <p>User ID: {userId}</p>
      <p>User Role: {userRole}</p>
      {profession && (
        <div>
          <h2>Profession Details</h2>
          <p>Profession Name: {profession.profession_name}</p>
          <p>Description: {profession.profession_description}</p>
        </div>
      )}

      <h2>Test Questions</h2>
      <form onSubmit={handleSubmit}>
        {testQuestions.map((question, index) => (
          <div key={index}>
            <p>{index + 1}. {question.question} Level: {question.level_of_question}</p>
            <ul>
              {Object.values(question).slice(0, 4).map((option, optionIndex) => (
                <li key={optionIndex}>
                  <label style={{ color: submitted && option === question.correct_answer ? 'green' : (submitted && answers[index] !== option ? 'red' : 'inherit') }}>
                    <input 
                      type="radio" 
                      name={`question${index}`} 
                      value={option} 
                      checked={answers[index] === option} 
                      onChange={() => handleAnswerChange(index, option)} 
                      disabled={submitted} // Disable input after submission
                    />
                    {option}
                  </label>
                  {submitted && answers[index] !== option && option === question.correct_answer && (
                    <span style={{ color: 'green' }}> ✓</span>
                  )}
                  {submitted && answers[index] === option && option !== question.correct_answer && (
                    <span style={{ color: 'red' }}> x</span>
                  )}
                </li>
              ))}
            </ul>
          </div>
        ))}
        <button type="submit" disabled={submitted}>Submit</button>
      </form>

      {mark !== null && (
        <div>
          <h2>Test Result</h2>
          <p>Mark: {mark}%</p>
          <p>Correct Answers: {correctAnswersCount}/{testQuestions.length}</p>
        </div>
      )}
    </div>
  );
}
