//UserCoursePage.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import XmlViewer from 'react-xml-viewer'; // Change import to default

const Recommendations = ({ resultId }) => {
  const [recommendations, setRecommendations] = useState('');
  const [userId, setUserId] = useState('');

  useEffect(() => {
    const userIdFromStorage = sessionStorage.getItem("user_id");
    setUserId(userIdFromStorage);

    const fetchRecommendations = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/get_user_xml/${userIdFromStorage}`);
        setRecommendations(response.data.user_xmls);
      } catch (error) {
        console.error('Error fetching XML file:', error);
      }
    };
    fetchRecommendations();
  }, [resultId]);

  // Function to render recommendations for each topic
  const renderRecommendations = () => {
    if (!recommendations) return null;

    // Parse XML to JSON
    const parser = new DOMParser();
    const xmlDoc = parser.parseFromString(recommendations, "text/xml");

    // Extract topics from XML
    const topics = xmlDoc.getElementsByTagName("topic");

    // Iterate over topics and render collapse items
    return Array.from(topics).map((topic, index) => {
      const title = topic.querySelector("title").textContent;
      const description = topic.querySelector("description").textContent;
      const recommendationElements = topic.querySelectorAll("recommendation");

      return (
        <div key={index} className="collapse-item">
          <button className="collapse-btn">{title}</button>
          <div className="collapse-content">
            <p>{description}</p>
            <ul>
              {Array.from(recommendationElements).map((recommendation, idx) => (
                <li key={idx}>{recommendation.textContent}</li>
              ))}
            </ul>
          </div>
        </div>
      );
    });
  };

  return (
    <div>
      {renderRecommendations()}
    </div>
  );
};

export default Recommendations;
