// UserTestPage.jsx

import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

export default function UserTestPage() {
    console.error("Error fetching profession details:", error);
  const { id } = useParams();
  const [profession, setProfession] = useState(null);
  const [testQuestions, setTestQuestions] = useState([]);

  useEffect(() => {
    // Fetch profession details
    fetch(`http://localhost:5000/get_prof/${id}`)
      .then((response) => response.json())
      .then((data) => {
        setProfession(data);
      })
      .catch((error) => console.error("Error fetching profession details:", error));

    // Fetch test questions for the profession
    fetch(`http://localhost:5000/get_test_questions`) // Update the URL here
    .then((response) => response.json())
    .then((data) => {
        setTestQuestions(data.profTestCreatedQuestions);
    })
    .catch((error) => console.error("Error fetching test questions:", error));

  }, [id]);

  return (
    <div>
      <h2>Profession Details</h2>
      {profession && (
        <div>
          <p>Profession ID: {profession.id}</p>
          <p>Profession Name: {profession.profession_name}</p>
          <p>Description: {profession.profession_description}</p>
        </div>
      )}

      <h2>Test Questions</h2>
      <ul>
        {testQuestions.map((question) => (
          <li key={question.id}>
            Question: {question.question}
            <br />
            Correct Answer: {question.correct_answer}
            <br />
            Answer Variants:
            <ul>
              <li>{question.answer_variant1}</li>
              <li>{question.answer_variant2}</li>
              <li>{question.answer_variant3}</li>
              <li>{question.answer_variant4}</li>
            </ul>
          </li>
        ))}
      </ul>
    </div>
  );
}
