import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

export default function UserMainPage() {
  const [professions, setProfessions] = useState([]);
  const [profTests, setProfTests] = useState([]);
  const [profTestCreatedQuestions, setProfTestCreatedQuestions] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/get_prof")
      .then((response) => response.json())
      .then((data) => {
        if (data.professions) {
          setProfessions(data.professions);
        }
      })
      .catch((error) => console.error("Error fetching professions:", error));

    fetch("http://localhost:5000/get_test")
      .then((response) => response.json())
      .then((data) => {
        if (data.profTest) {
          setProfTests(data.profTest);
        }
      })
      .catch((error) => console.error("Error fetching profTests:", error));

    fetch("http://localhost:5000/get_test_questions")
      .then((response) => response.json())
      .then((data) => {
        if (data.profTestCreatedQuestions) {
          setProfTestCreatedQuestions(data.profTestCreatedQuestions);
        }
      })
      .catch((error) => console.error("Error fetching profTestQuestions:", error));
  }, []);

  return (
    <div>
      <div className="container h-100">
        <div className="row h-100">
          <div className="col-12">
            <h1>Welcome to this User</h1>
            <p>
              <Link to="/login" className="btn btn-success">
                Login
              </Link>{" "}
              |{" "}
              <Link to="/register" className="btn btn-success">
                register
              </Link>{" "}
            </p>
            {/* Display Professions */}
            <h2>Professions List</h2>
            <ul>
              {professions.map((prof) => (
                <li key={prof.id}>
                  (Profession ID: {prof.id}) (Profession Name: {prof.profession_name}) (Description: {prof.profession_description})
                </li>
              ))}
            </ul>

            {/* Display ProfTestQuestions */}
            <h2>profTestCreatedQuestions List</h2>
            <ul>
              {profTestCreatedQuestions.map((question) => (
                <li key={question.id}>
                  (ProfTest ID: {question.prof_test_id}) (Question: {question.question}) (Correct Answer: {question.correct_answer})
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
}
