import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

export default function AdminManageUsersPage() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    // Fetch users from backend when the component mounts
    fetch("http://localhost:5000/get_users")
      .then((response) => response.json())
      .then((data) => {
        if (data.users) {
          setUsers(data.users);
        }
      })
      .catch((error) => console.error("Error fetching users:", error));
  }, []); // Empty dependency array means this effect runs once when component mounts

    // Add these functions
    const handleDeleteUser = (userId) => {
        // Make a DELETE request to delete the user
        fetch(`http://localhost:5000/delete_user/${userId}`, {
        method: 'DELETE',
        })
        .then((response) => response.json())
        .then((data) => {
            console.log(data);
            // Refresh the user list after deletion
            setUsers(users.filter((user) => user.id !== userId));
        })
        .catch((error) => console.error('Error deleting user:', error));
    };
    
    const handleModifyUser = (userId) => {
        // Redirect or open a modal for modifying user details
        // You can implement this based on your application's design
        console.log(`Modify user with ID ${userId}`);
    };

  return (
    <div>
      <div className="container h-100">
        <div className="row h-100">
          <div className="col-12">
            <h1>Welcome to Users Manage</h1>
            <p>
            <p><Link to="/login" className="btn btn-success">Login</Link> | <Link to="/register" className="btn btn-success">register</Link> </p>
                    <p><Link to="/admin_users" className="btn btn-success">Manage Users</Link> | <Link to="/admin_prof" className="btn btn-success">Manage professions</Link></p>
            </p>

            {/* Display Users */}
            <h2>Users List</h2>
            <ul>
                {users.map((user) => (
                <li key={user.id}>
                (User email:{user.email}) (Role ID: {user.role_id})
                <button onClick={() => handleDeleteUser(user.id)}>Delete</button>
                <button onClick={() => handleModifyUser(user.id)}>Modify</button>
                </li>
            ))}
            </ul>

            {/* Add New User Form (example, modify as needed) */}
            <h2>Add New User</h2>
            <form method="post" action="http://localhost:5000/add_user">

              <label>Email:</label>
              <input type="text" name="email" required />
              <label>Password:</label>
              <input type="password" name="password" required />
              <label>Role ID:</label>
              <input type="number" name="role_id" />
              <button type="submit">Add User</button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}
