// UserTestResultsPage.jsx
import React, { useState, useEffect, useRef } from "react";
import { Link } from "react-router-dom";
import { useUserRoleAccess } from "../Functions/apiUtils";

export default function UserTestResultsPage() {
    const checkUserRoleAccess = useUserRoleAccess(0);
    const [userEmail, setUserEmail] = useState("");
    const [userId, setUserId] = useState("");
    const [userRole, setUserRole] = useState("");
    const [userAnswers, setUserAnswers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    
    // Define a ref to track previous userId
    const previousUserId = useRef(null);

    useEffect(() => {
        // Fetch user information from session storage or state
        const userEmailFromStorage = sessionStorage.getItem("user_email");
        const userIdFromStorage = sessionStorage.getItem("user_id");
        const userRoleFromStorage = sessionStorage.getItem("user_role");
    
        setUserEmail(userEmailFromStorage);
        setUserId(userIdFromStorage);
        setUserRole(userRoleFromStorage);
    
        if (!checkUserRoleAccess() || !userId) {
            setLoading(false);
            return;
        }
    
        // Fetch user answers from the backend API only if userId has changed
        if (userId !== previousUserId.current) {
            setLoading(true);
            fetch(`http://localhost:5000/user_answers/${userId}`)
                .then((response) => {
                    if (!response.ok) {
                        throw new Error("Failed to fetch user answers");
                    }
                    return response.json();
                })
                .then((data) => {
                    setUserAnswers(data.userAnswers);
                    setLoading(false);
                })
                .catch((error) => {
                    setError(error);
                    setLoading(false);
                });
            previousUserId.current = userId; // Update previousUserId ref
        }
    }, [userId, checkUserRoleAccess]);

    const handleDeleteTestResults = () => {
        fetch("http://localhost:5000/delete_all_test_results", {
            method: "DELETE",
        })
        .then((response) => {
            if (!response.ok) {
                throw new Error("Failed to delete test results");
            }
            return response.json();
        })
        .then((data) => {
            // Optionally, you can update state or perform any additional actions
            console.log(data.message);
        })
        .catch((error) => {
            setError(error);
        });
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error.message}</div>;
    }

    // Group user answers by profession test ID
    const groupedUserAnswers = {};
    userAnswers.forEach((answer) => {
        if (!groupedUserAnswers[answer.prof_test_id]) {
            groupedUserAnswers[answer.prof_test_id] = [];
        }
        groupedUserAnswers[answer.prof_test_id].push(answer);
    });

    return (
        <div>
            <div className="container h-100">
                <div className="row h-100">
                    <div className="col-12">
                        <h1>Welcome to this User</h1>
                        <p>User Email: {userEmail}</p>
                        <p>User ID: {userId}</p>
                        <p>User Role: {userRole}</p>
                        
                        {/* Display user answers grouped by profession test */}
                        {Object.entries(groupedUserAnswers).map(([testId, answers]) => (
                            <div key={testId}>
                                <h2>Profession Test ID: {testId}</h2>
                                <ul>
                                    {answers.map((answer) => (
                                        <li key={answer.id}>
                                            Question: {answer.question_text}, Level: {answer.question_level}, User Answer: {answer.user_answer}, Correct/Incorrect: {answer.correct_incorrect === 1 ? "Correct" : "Incorrect"}
                                        </li>
                                    ))}
                                </ul>
                            </div>
                        ))}

                        {/* Button to delete all test results */}
                        <button onClick={handleDeleteTestResults}>Delete All Test Results</button>
                    </div>
                </div>
            </div>
        </div>
    );
}
