import React from "react";
import { Link } from "react-router-dom";
import AdminManagePage from "./Admin/AdminManagePage";

export default function LandingPage() {
  // Define a function to render the form
  const renderForm = () => {
    return (
      <form method="post" action="http://localhost:5000/add_prof">
        <label>Profession Name:</label>
        <input type="text" name="profession_name" required />
        <label>Description:</label>
        <input type="text" name="profession_description" />
        <button type="submit">Add Profession</button>
      </form>
    );
  };

  return (
    <div>
      <div className="container h-100">
        <div className="row h-100">
          <div className="col-12">
            <h1>Welcome to this React Application</h1>
            <p>
              <Link to="/login" className="btn btn-success">
                Login
              </Link>{" "}
              |{" "}
              <Link to="/register" className="btn btn-success">
                Register
              </Link>{" "}
            </p>

            {/* Display Professions using AdminManagePage */}
            <AdminManagePage
              pageTitle="Professions List"
              apiEndpoint="http://localhost:5000/get_prof"
              dataKey="professions"
              renderItem={(prof) => (
                <>
                  (Profession Name: {prof.profession_name}) (Description: {prof.profession_description})
                </>
              )}
              renderForm={renderForm} 
            />
          </div>
        </div>
      </div>
    </div>
  );
}
