import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { getUserInfo } from "../Functions/authUtils";

const UserCoursePage = () => {
  const [courseOutline, setCourseOutline] = useState(null);
  const { userId } = getUserInfo(); // Retrieve user ID

  useEffect(() => {
    // Fetch JSON data from your backend API
    axios.get(`http://localhost:5000/get_user_json/${userId}`) // Assuming your backend endpoint is '/get_user_json/:userId'
      .then(response => {
        setCourseOutline(response.data.user_jsons); // Assuming your JSON data is structured as { user_jsons: [...] }
      })
      .catch(error => {
        console.error('Error fetching course outline:', error);
      });
  }, [userId]); // Make sure to include userId in the dependency array

  // Helper function to format recommendations
  return (
    <div>
      <h1>Course Page</h1>
      {courseOutline &&
        courseOutline.map((outline, index) => (
          <div key={index}>
            {outline.course_outline &&
              outline.course_outline.profession_description && (
                <>
                  <h2>
                    {outline.course_outline.profession_description.name} Course
                    Outline
                  </h2>
                  <h3>Profession Description:</h3>
                  <p>
                    {outline.course_outline.profession_description.description}
                  </p>
                </>
              )}
            {outline.course_outline &&
              outline.course_outline.topics &&
              outline.course_outline.topics.map((topic, index) => (
                formatRecommendations(topic, index)
              ))}
            {outline.course_outline &&
              outline.course_outline.general_comments && (
                <>
                  <h3>General Comments:</h3>
                  <p>{outline.course_outline.general_comments.comment}</p>
                  <h3>Improvement Recommendations:</h3>
                  <p>
                    {outline.course_outline.general_comments.improvement_recommendations}
                  </p>
                </>
              )}
          </div>
        ))}
    </div>
  );
  
  

  return (
    <div>
      <h1>Course Page</h1>
      {courseOutline && courseOutline.map((outline, index) => (
        <div key={index}>
          <h2>{outline.course_outline.profession_description.name} Course Outline</h2>
          <h3>Profession Description:</h3>
          <p>{outline.course_outline.profession_description.description}</p>
          <h3>Topics:</h3>
          {outline.course_outline.topics.map((topic, index) => (
            formatRecommendations(topic)
          ))}
          <h3>General Comments:</h3>
          <p>{outline.course_outline.general_comments.comment}</p>
          <h3>Improvement Recommendations:</h3>
          <p>{outline.course_outline.general_comments.improvement_recommendations}</p>
        </div>
      ))}
    </div>
  );
};

export default UserCoursePage;
