// AdminManageUsersPage.jsx
import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { fetchData, renderListItemsWithActions, deleteItem, modifyItem } from "../Functions/apiUtils";

export default function AdminManageUsersPage() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    fetchData("http://localhost:5000/get_users", setUsers, "users");
  }, []);

  const renderUserItem = (user) => (
    <React.Fragment>
      (User email: {user.email}) (Role ID: {user.role_id})
    </React.Fragment>
  );

  const deleteUser = async (userId) => {
    try {
      await deleteItem("http://localhost:5000/delete_user", userId, setUsers, users, "users");
    } catch (error) {
      console.error(`Error deleting user with ID ${userId}:`, error);
    }
  };

  const modifyUser = async (user) => {
    try {


      await modifyItem("http://localhost:5000/modify_user", modifiedUser, setUsers, users, "users");
    } catch (error) {
      console.error(`Error modifying user with ID ${user.id}:`, error);
    }
  };

  return (
    <div>
      <div className="container h-100">
        <div className="row h-100">
          <div className="col-12">
            <h1>Welcome to Users Management</h1>
            <p>
              <Link to="/login" className="btn btn-success">
                Login
              </Link>{" "}
              |{" "}
              <Link to="/register" className="btn btn-success">
                Register
              </Link>
            </p>
            <p>
              <Link to="/admin_users" className="btn btn-success">
                Manage Users
              </Link>{" "}
              |{" "}
              <Link to="/admin_prof" className="btn btn-success">
                Manage Professions
              </Link>
            </p>

            {/* Display Users with Actions */}
            <h2>Users List</h2>
            {renderListItemsWithActions(users, renderUserItem, deleteUser, modifyUser)}

            {/* Add New User Form (example, modify as needed) */}
            <h2>Add New User</h2>
            <form method="post" action="http://localhost:5000/add_user">
              <label>Email:</label>
              <input type="text" name="email" required />
              <label>Password:</label>
              <input type="password" name="password" required />
              <label>Role ID:</label>
              <input type="number" name="role_id" />
              <button type="submit">Add User</button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}
