// AdminManageUsersPage.jsx
import React from "react";
import AdminManagePage from "./AdminManagePage";

class AdminManageUsersPage extends React.Component {
    render() {
      return (
        <AdminManagePage
          pageTitle="Welcome to Users Manage"
          listTitle="Users List"
          formTitle="Add New User"
          apiEndpoint="http://localhost:5000/get_users"
          dataKey="users"
          renderItem={(user) => (
            <>
              (User email: {user.email}) (Role ID: {user.role_id})
            </>
          )}
          renderForm={() => (
            <form method="post" action="http://localhost:5000/add_user">
              <label>Email:</label>
              <input type="text" name="email" required />
              <label>Password:</label>
              <input type="password" name="password" required />
              <label>Role ID:</label>
              <input type="number" name="role_id" />
              <button type="submit">Add User</button>
            </form>
          )}
        />
      );
    }
  }
  

export default AdminManageUsersPage;
