import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

export default function UserTestPage() {
  const { id } = useParams();
  const [profession, setProfession] = useState(null);
  const [testQuestions, setTestQuestions] = useState([]);

  useEffect(() => {
    console.log("Fetching profession details for ID:", id);
    fetch(`http://localhost:5000/get_prof/${id}`)
      .then((response) => response.json())
      .then((data) => {
        console.log("Profession details fetched:", data);
        setProfession(data.professions);
      })
      .catch((error) => console.error("Error fetching profession details:", error));

    console.log("Fetching test questions for profession ID:", id);
    fetch(`http://localhost:5000/get_test_questions_by_profession/${id}`)
      .then((response) => response.json())
      .then((data) => {
        console.log("Test questions fetched:", data);
        setTestQuestions(data.profTestCreatedQuestions);
      })
      .catch((error) => console.error("Error fetching test questions:", error));
  }, [id]);

  const handleSubmit = (event) => {
    event.preventDefault();
    // You can implement submission logic here, e.g., checking answers
    console.log("Form submitted!");
  };

  return (
    <div>
            <h2>Professions List</h2>
            <ul>
              {profession.map((prof) => (
                <li key={prof.id}>
                  (Profession ID: {prof.id}) (Profession Name: {prof.profession_name}) (Description: {prof.profession_description})
                </li>
              ))}
            </ul>

        <h2>Test Questions</h2>
        <form onSubmit={handleSubmit}>
        {testQuestions && testQuestions.map((question, index) => ( // Add a check for testQuestions
            <div key={question.question_id}>
            <p>Question {index + 1}: {question.question}</p>
            <div>
                {['answer_variant1', 'answer_variant2', 'answer_variant3', 'answer_variant4'].map((variant, i) => (
                <div key={i}>
                    <input type="radio" id={`${question.question_id}_${variant}`} name={`question_${question.question_id}`} value={question[variant]} />
                    <label htmlFor={`${question.question_id}_${variant}`}>{question[variant]}</label>
                </div>
                ))}
            </div>
            </div>
        ))}
        <button type="submit">Submit Answers</button>
        </form>
    </div>
    );

}
